          />
          <h3 className="text-xl font-semibold mb-2 text-gray-900 dark:text-white">
            No Alarms Yet
          </h3>
          <p className="text-gray-600 dark:text-gray-400 mb-6">
            Create your first alarm to get started with smart wake-ups.
          </p>
        </div>
      </main>
    );
  }

  return (
